import React from "react";
import { signUp } from "../api/apiCalls";
class UserSignupPage extends React.Component {
  state = {
    username: null,
    displayName: null,
    password: null,
    passwordRepeat: null,
    pendingApiCall: null,
    errors : {}
  };

  onChange = (event) => {
    const { name, value } = event.target;

    // const value = event.target.value;
    // const name = event.target.name;

    this.setState({
      [name]: value,
    });
  };

  onClickSignUp = async (event) => {
    event.preventDefault();
    const { username, displayName, password } = this.state;

    const body = {
      username,
      displayName,
      password,
    };

    this.setState({ pendingApiCall: true });
    try {
      const response = await signUp(body);
    } catch (error) {
      this.setState({errors : error.response.data.validationErrors})
    }
    this.setState({ pendingApiCall: false });
    // signUp(body)
    //   .then((response) => {
    //     this.setState({ pendingApiCall: false });
    //   })
    //   .catch((error) => {
    //     this.setState({ pendingApiCall: false });
    //   });
  };

  // onChangeUserName = event => {
  //   this.setState({
  //     username : event.target.value
  //   });
  // };

  // onChangeDisplayName = event => {
  //   this.setState({
  //     displayName : event.target.value
  //   });
  // };

  // onChangePassword= event => {
  //   this.setState({
  //     password : event.target.value
  //   });
  // };

  // onChangePasswordRepeat = event => {
  //   this.setState({
  //     passwordRepeat : event.target.value
  //   });
  // };

  render() {
    const { pendingApiCall, errors } = this.state;
    const {username} = errors;
    return (
      <div className="container">
        <form>
          <figure className="text-center">
            <blockquote className="blockquote">
              <br></br>
              <h2>Sign Up</h2>
            </blockquote>
            <figcaption className="blockquote-footer">
              Dünyada her şey için, medeniyet için, hayat için, muvaffakiyet
              için en hakiki mürşit ilimdir , fendir. İlim ve fennin haricinde
              mürşit aramak gaflettir, cehalettir, dalalettir.{" "}
              <cite title="Source Title">M.Kemal Atatürk</cite>
            </figcaption>
          </figure>
          <div className="form-group">
            <label>Username</label>
            <input
              name="username"
              onChange={this.onChange}
              className={username ? "form-control is-invalid" : "form-control" }
            />
            <div className="invalid-feedback">{username}</div>
          </div>
          <div>
            <label>Display Name</label>
            <input
              name="displayName"
              onChange={this.onChange}
              className="form-control"
            />
          </div>
          <div>
            <label>Password</label>
            <input
              name="password"
              type="password"
              onChange={this.onChange}
              className="form-control"
            />
          </div>
          <div>
            <label>Password Repeat</label>
            <input
              name="passwordRepeat"
              type="password"
              onChange={this.onChange}
              className="form-control"
            />
          </div>
          <div className="text-center mt-3">
            <button
              className="btn btn-primary"
              onClick={this.onClickSignUp}
              disabled={pendingApiCall}
            >
              {pendingApiCall && (
                <span className="spinner-grow spinner-grow-sm"></span>
              )}
              Sign Up
            </button>
          </div>
        </form>
      </div>
    );
  }
}

export default UserSignupPage;
